import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../axiosConfig';
import { FaUserCircle, FaCog, FaEnvelope } from 'react-icons/fa';
import {
    Box,
    Button,
    Divider,
    Typography,
    IconButton,
    List,
    ListItem,
    ListItemText,
    CircularProgress,
    Modal,
    TextField,
} from '@mui/material';
import { styled } from '@mui/system';

const StyledContainer = styled(Box)({
    display: 'flex',
    flexDirection: 'row',
    padding: '0',
    margin: '0',
    width: '100vw',
    height: '100vh',
    position: 'fixed',
    top: 0,
    left: 0,
    overflow: 'hidden',
    backgroundColor: '#DDE6ED',
});

const Sidebar = styled(Box)(({ theme }) => ({
    width: '20%',
    maxWidth: '350px',
    height: '100vh',
    borderRight: '1px solid #ccc',
    padding: '20px',
    backgroundColor: '#c3d0d9',
    flexShrink: 0,
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)',
    [theme.breakpoints.down('sm')]: {
        width: '30%',
    },
    [theme.breakpoints.down('xs')]: {
        display: 'none',
    },
}));

const MainContent = styled(Box)({
    flexGrow: 1,
    height: '100vh',
    padding: '20px',
    textAlign: 'center',
    backgroundColor: '#ffffff',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    overflowY: 'auto',
    borderRadius: '10px',
    margin: '20px',
});

const Suggestions = styled(Box)(({ theme }) => ({
    width: '30%', // Increased width to 30%
    maxWidth: '350px', // Optional: Set a maximum width
    height: '100vh',
    borderLeft: '1px solid #ccc',
    padding: '20px',
    backgroundColor: '#c3d0d9',
    flexShrink: 0,
    boxShadow: '-2px 0 5px rgba(0, 0, 0, 0.1)',
    display: 'flex', // Ensure the panel displays correctly
    flexDirection: 'column', // Maintain column layout
    [theme.breakpoints.down('sm')]: {
        width: '40%', // Adjust width on smaller screens if necessary
    },
    [theme.breakpoints.down('xs')]: {
        display: 'none',
    },
}));


const ContentBox = styled(Box)({
    backgroundColor: '#ffffff',
    padding: '15px',
    borderRadius: '10px',
    boxShadow: '0px 2px 5px rgba(0, 0, 0, 0.1)',
    marginBottom: '10px',
});

const SidebarButton = styled(IconButton)({
    marginBottom: '10px',
});

const Dashboard = () => {
    const [userData, setUserData] = useState(null);
    const [role, setRole] = useState('');
    const [friends, setFriends] = useState([]);
    const [peopleYouMayKnow, setPeopleYouMayKnow] = useState([]);
    const [settingsVisible, setSettingsVisible] = useState(false);
    const [loading, setLoading] = useState(true);
    const [messageModalOpen, setMessageModalOpen] = useState(false);
    const [selectedFriend, setSelectedFriend] = useState(null);
    const [messageText, setMessageText] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('token');
            const userId = JSON.parse(localStorage.getItem('user'))?.id;

            if (token && userId) {
                try {
                    const response = await axiosInstance.get(`/api/user/id/${userId}`, {
                        headers: { Authorization: `Bearer ${token}` },
                    });
                    setUserData(response.data);
                    setRole(response.data.roles?.[0] || 'Guest');
                    await fetchFriends(userId, token);
                    await fetchPeopleYouMayKnow(userId, token);
                } catch (error) {
                    console.error('Failed to fetch user data:', error.response ? error.response.data : error.message);
                    navigate('/login');
                } finally {
                    setLoading(false);
                }
            } else {
                navigate('/login');
            }
        };

        fetchUserData();
    }, [navigate]);

    const fetchFriends = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/api/user/friends/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setFriends(response.data);
        } catch (error) {
            console.error('Failed to fetch friends:', error.response ? error.response.data : error.message);
        }
    };

    const fetchPeopleYouMayKnow = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/user-service/api/friend-request/not-friends/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setPeopleYouMayKnow(response.data);
        } catch (error) {
            console.error('Failed to fetch people you may know:', error.response ? error.response.data : error.message);
        }
    };

    const handleSendMessage = (friend) => {
        setSelectedFriend(friend);
        setMessageModalOpen(true);
    };

    const handleSendMessageToBackend = async () => {
        const token = localStorage.getItem('token');
        const senderId = JSON.parse(localStorage.getItem('user'))?.id;

        if (token && senderId && selectedFriend && messageText.trim()) {
            try {
                const messageData = {
                    senderId,
                    recipientId: selectedFriend.id,
                    content: messageText,
                };
                await axiosInstance.post('/api/message', messageData, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                alert('Message sent successfully!');
                setMessageText('');
                setMessageModalOpen(false);
            } catch (error) {
                console.error('Failed to send message:', error.response ? error.response.data : error.message);
                alert('Failed to send message. Please try again later.');
            }
        }
    };

    if (loading) {
        return <CircularProgress />;
    }

    return (
        <StyledContainer>
            <Sidebar>
                <div>
                    <SidebarButton onClick={() => navigate('/profile')}>
                        <FaUserCircle size={50} />
                    </SidebarButton>
                    <Typography variant="h5">{userData ? userData.username : 'Guest'}</Typography>
                    <Divider sx={{ margin: '10px 0' }} />

                    <ContentBox>
                        <Typography variant="h6" sx={{ color: '#27374D' }}>Friends List</Typography>
                        <List>
                            {friends.length > 0 ? (
                                friends.map((friend) => (
                                    <ListItem key={friend.id}>
                                        <ListItemText primary={`${friend.firstname} ${friend.lastname}`} />
                                        <Button
                                            variant="outlined"
                                            size="small"
                                            startIcon={<FaEnvelope />}
                                            onClick={() => handleSendMessage(friend)}
                                            sx={{ marginLeft: '10px' }}
                                        >
                                            Message
                                        </Button>
                                    </ListItem>
                                ))
                            ) : (
                                <ListItem>
                                    <ListItemText primary="No friends found." />
                                </ListItem>
                            )}
                        </List>
                    </ContentBox>
                </div>
                <div>
                    <SidebarButton onClick={toggleSettings}>
                        <FaCog size={20} />
                    </SidebarButton>

                    {settingsVisible && (
                        <Box>
                            <Button onClick={handleUpdateProfile} variant="outlined" sx={{ marginBottom: '5px' }}>
                                Update Profile
                            </Button>
                            <Button onClick={handleLogout} variant="outlined">
                                Logout
                            </Button>
                        </Box>
                    )}
                </div>
            </Sidebar>

            <MainContent>
                <Typography variant="h4" sx={{ color: '#27374D' }}>Welcome to your Dashboard!</Typography>
                <Typography variant="body1" sx={{ margin: '20px 0' }}>
                    This area can be used for various widgets or information.
                </Typography>
            </MainContent>

            <Suggestions>
                <ContentBox>
                    <Typography variant="h6" sx={{ color: '#27374D' }}>People You May Know</Typography>
                    <List>
                        {peopleYouMayKnow.length > 0 ? (
                            peopleYouMayKnow.map((user) => (
                                <ListItem key={user.id}>
                                    <ListItemText primary={user.username} />
                                    <Button 
                                        variant="outlined" 
                                        size="small" 
                                        onClick={() => handleSendFriendRequest(user.id)} 
                                        sx={{ marginLeft: '10px' }}
                                    >
                                        Send Request
                                    </Button>
                                </ListItem>
                            ))
                        ) : (
                            <ListItem>
                                <ListItemText primary="No suggestions." />
                            </ListItem>
                        )}
                    </List>
                </ContentBox>
                <Button
                    onClick={handleGoToInvitations}
                    variant="outlined"
                    fullWidth
                    sx={{
                        backgroundColor: '#ffffff', // Set background color for button
                        marginTop: '10px',
                    }}
                >
                    Go to Invitations
                </Button>
            </Suggestions>

            {/* Modal for Sending Message */}
            <Modal
                open={messageModalOpen}
                onClose={() => setMessageModalOpen(false)}
                aria-labelledby="send-message-modal"
                aria-describedby="send-message-to-friend"
            >
                <Box
                    sx={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        width: 400,
                        bgcolor: 'background.paper',
                        border: '2px solid #000',
                        boxShadow: 24,
                        p: 4,
                    }}
                >
                    <Typography variant="h6" component="h2">
                        Send a message to {selectedFriend?.firstname} {selectedFriend?.lastname}
                    </Typography>
                    <TextField
                        label="Your Message"
                        multiline
                        rows={4}
                        fullWidth
                        value={messageText}
                        onChange={(e) => setMessageText(e.target.value)}
                        sx={{ marginTop: 2 }}
                    />
                    <Button
                        onClick={handleSendMessageToBackend}
                        variant="contained"
                        sx={{ marginTop: 2 }}
                    >
                        Send Message
                    </Button>
                </Box>
            </Modal>
        </StyledContainer>
    );
};

export default Dashboard;
