import React, { useEffect, useState } from 'react';
import axiosInstance from '../axiosConfig';

const UserProfile = () => {
    const [userData, setUserData] = useState(null);
    const [loading, setLoading] = useState(true); // State to manage loading status
    const navigate = useNavigate();

     // Fetch user data and friends on component mount
useEffect(() => {
    const fetchUserData = async () => {
        const token = localStorage.getItem('token');
        const userId = JSON.parse(localStorage.getItem('user'))?.id;

        if (token && userId) {
            try {
                const response = await axiosInstance.get(`/user/id/${userId}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                const fetchedUserData = response.data;
                setUserData(fetchedUserData);

                // Safely set the role if roles exist
                const userRole = fetchedUserData.roles && fetchedUserData.roles.length > 0 ? fetchedUserData.roles[0] : 'Guest';
                setRole(userRole);

                // Fetch friends list using token
                await fetchFriends(fetchedUserData.id, token);

                // Update user data in localStorage
                localStorage.setItem('user', JSON.stringify(fetchedUserData)); 
            } catch (error) {
                console.error('Failed to fetch user data:', error.response ? error.response.data : error.message);
                navigate('/login'); // Redirect to login if not authenticated or error
            }
        } else {
            console.error('No user data found, redirecting to login.');
            navigate('/login');
        }
    };

    fetchUserData();
}, [navigate]);

    if (loading) {
        return <p>Loading user data...</p>; // Display loading message
    }

    return (
        <div>
            <h2>User Profile</h2>
            {userData ? (
                <div>
                    <p>Username: {userData.username}</p>
                    <p>Email: {userData.email}</p>
                    <p>Full Name: {userData.firstName} {userData.lastName}</p>
                    <p>Phone Number: {userData.phoneNumber}</p>
                    <p>Bio: {userData.bio}</p>
                    <p>Role: {userData.roles.length > 0 ? userData.roles[0] : 'No role assigned'}</p> {/* Display the role */}
                    <p>Friends: {userData.friends ? userData.friends.map(friend => friend.username).join(', ') : 'No friends found'}</p>
                    {/* Add more user information as needed */}
                </div>
            ) : (
                <p>No user data available.</p>
            )}
        </div>
    );
};

export default UserProfile;
