import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../axiosConfig';

const UpdateProfile = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        firstname: '',
        lastname: '',
        phoneNumber: ''
    });
    const navigate = useNavigate();

    // Fetch user data from local storage when component loads
    useEffect(() => {
        const user = JSON.parse(localStorage.getItem('user')); // Get user from localStorage
        const token = localStorage.getItem('token'); // Get token from localStorage

        if (user && token) {
            setFormData({
                username: user.username,
                email: user.email,
                firstname: user.firstname,
                lastname: user.lastname,
                phoneNumber: user.phoneNumber || ''
            });
        } else {
            console.error('User or token not found, redirecting to login.');
            navigate('/login'); // Redirect to login if no user or token found
        }
    }, [navigate]);

    const handleInputChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token'); // Retrieve token
        try {
            // Make an authenticated API request to update profile
            const response = await axiosInstance.put('/user/update', formData, {
                headers: {
                    Authorization: `Bearer ${token}`, // Pass the token in Authorization header
                },
            });

            console.log('Profile updated:', response.data);

            // Update user data in localStorage with the response data
            localStorage.setItem('user', JSON.stringify(response.data));

            // Redirect to dashboard after profile update
            navigate('/dashboard');
        } catch (error) {
            console.error('Failed to update profile:', error.response ? error.response.data : error.message);
            if (error.response && error.response.status === 401) {
                console.error('Unauthorized: Invalid or expired token, redirecting to login.');
                navigate('/login'); // Redirect to login if unauthorized
            }
        }
    };


    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
                placeholder="Username"
            />
            <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="Email"
            />
            <input
                type="text"
                name="firstname"
                value={formData.firstname}
                onChange={handleInputChange}
                placeholder="First Name"
            />
            <input
                type="text"
                name="lastname"
                value={formData.lastname}
                onChange={handleInputChange}
                placeholder="Last Name"
            />
            <input
                type="text"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleInputChange}
                placeholder="Phone Number"
            />
            <button type="submit">Update Profile</button>
        </form>
    );
};

export default UpdateProfile;
