import React, { useEffect, useState } from 'react';
import axiosInstance from '../axiosConfig';

const UserProfile = () => {
    const [userData, setUserData] = useState(null);
    const [loading, setLoading] = useState(true); // State to manage loading status

    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('token'); // Get the token from localStorage
            const user = JSON.parse(localStorage.getItem('user'));

            if (user && user.id) {
                try {
                    const response = await axiosInstance.get(`/user/id/${user.id}`, {
                        headers: {
                            Authorization: `Bearer ${token}`, // Pass the token in the header
                        },
                    });
                    setUserData(response.data); // Set the fetched user data in the state
                } catch (error) {
                    console.error('Failed to fetch user data:', error.response ? error.response.data : error.message);
                } finally {
                    setLoading(false); // Set loading to false after fetching
                }
            } else {
                console.error('No user data found, user not authenticated.');
                setLoading(false); // Ensure loading is false if no user data
            }
        };

        fetchUserData();
    }, []);

    if (loading) {
        return <p>Loading user data...</p>; // Display loading message
    }

    return (
        <div>
            <h2>User Profile</h2>
            {userData ? (
                <div>
                    <p>Username: {userData.username}</p>
                    <p>Email: {userData.email}</p>
                    <p>Full Name: {userData.firstName} {userData.lastName}</p>
                    <p>Phone Number: {userData.phoneNumber}</p>
                    <p>Bio: {userData.bio}</p>
                    <p>Role: {userData.roles.length > 0 ? userData.roles[0] : 'No role assigned'}</p> {/* Display the role */}
                    <p>Friends: {userData.friends ? userData.friends.map(friend => friend.username).join(', ') : 'No friends found'}</p>
                    {/* Add more user information as needed */}
                </div>
            ) : (
                <p>No user data available.</p>
            )}
        </div>
    );
};

export default UserProfile;
