import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../axiosConfig';
import { FaUserCircle, FaCog } from 'react-icons/fa';

const Dashboard = () => {
    const [userData, setUserData] = useState(null);
    const [role, setRole] = useState('');
    const [friends, setFriends] = useState([]); // State for the friends list
    const [settingsVisible, setSettingsVisible] = useState(false); // State for settings visibility
    const navigate = useNavigate();

   // Fetch user data and friends on component mount
useEffect(() => {
    const fetchUserData = async () => {
        const token = localStorage.getItem('token');
        const userId = JSON.parse(localStorage.getItem('user'))?.id;

        if (token && userId) {
            try {
                const response = await axiosInstance.get(`/user/id/${userId}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                const fetchedUserData = response.data;
                setUserData(fetchedUserData);

                // Safely set the role if roles exist
                const userRole = fetchedUserData.roles && fetchedUserData.roles.length > 0 ? fetchedUserData.roles[0] : 'Guest';
                setRole(userRole);

                // Fetch friends list using token
                await fetchFriends(fetchedUserData.id, token);

                // Update user data in localStorage
                localStorage.setItem('user', JSON.stringify(fetchedUserData)); 
            } catch (error) {
                console.error('Failed to fetch user data:', error.response ? error.response.data : error.message);
                navigate('/login'); // Redirect to login if not authenticated or error
            }
        } else {
            console.error('No user data found, redirecting to login.');
            navigate('/login');
        }
    };

    fetchUserData();
}, [navigate]);



    // Function to fetch friends list from the server
    const fetchFriends = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/user/friends/${userId}`, {
                headers: {
                    Authorization: `Bearer ${token}`, // Attach the token in request header
                },
            });
            setFriends(response.data); // Set the fetched friends data
        } catch (error) {
            console.error('Failed to fetch friends:', error.response ? error.response.data : error.message);
        }
    };

    // Navigate to profile page
    const goToProfile = () => {
        navigate('/profile');
    };

    // Handle logout action
    const handleLogout = () => {
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        navigate('/login');
    };

    // Navigate to update profile page
    const handleUpdateProfile = () => {
        navigate('/update-profile');
    };

    // Handle delete account action
    const handleDeleteAccount = async () => {
        const token = localStorage.getItem('token');
        if (userData && token) {
            try {
                await axiosInstance.delete(`/user/id/${userData.id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`, // Attach the token in request header
                    },
                });
                handleLogout(); // Logout after account deletion
            } catch (error) {
                console.error('Failed to delete account:', error.response ? error.response.data : error.message);
            }
        }
    };

    // Toggle the visibility of the settings menu
    const toggleSettings = () => {
        setSettingsVisible(!settingsVisible);
    };

    return (
        <div style={{ display: 'flex' }}>
            {/* Sidebar for User Info and Settings */}
            <div style={{ width: '250px', borderRight: '1px solid #ccc', padding: '20px' }}>
                <FaUserCircle size={50} onClick={goToProfile} style={{ cursor: 'pointer', marginBottom: '10px' }} />
                <h3>{userData ? `${userData.username}` : 'Guest'}</h3>

                {/* Friends List */}
                <h4>Friends List</h4>
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {friends.length > 0 ? (
                        friends.map((friend) => (
                            <li key={friend.id}>{friend.firstname} {friend.lastname}</li>
                        ))
                    ) : (
                        <li>No friends found.</li>
                    )}
                </ul>

                {/* Settings Icon and Menu */}
                <FaCog 
                    size={20} 
                    onClick={toggleSettings} 
                    style={{ cursor: 'pointer', marginTop: '10px' }} 
                />
                {settingsVisible && (
                    <div style={{ marginTop: '10px' }}>
                        <button onClick={handleUpdateProfile}>Update Profile</button>
                        <button onClick={handleDeleteAccount}>Delete Account</button>
                        <button onClick={handleLogout}>Logout</button>
                    </div>
                )}
            </div>

            {/* Main Dashboard Content */}
            <div style={{ padding: '20px', flex: 1 }}>
                <h2>{role === 'ROLE_ADMIN' ? 'Admin Dashboard' : 'User Dashboard'}</h2>

                {userData ? (
                    <div>
                        <p><strong>Username:</strong> {userData.username}</p>
                        <p><strong>Email:</strong> {userData.email}</p>
                        <p><strong>Full Name:</strong> {userData.firstname} {userData.lastname}</p>
                        <p><strong>Date of Birth:</strong> {userData.birthdate || 'N/A'}</p> {/* Display birthdate */}
                    </div>
                ) : (
                    <p>Loading user data...</p>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
