import React, { useEffect, useState } from 'react';
import axiosInstance from '../axiosConfig';
import { useNavigate } from 'react-router-dom';

const UpdateProfile = () => {
    const [userData, setUserData] = useState({
        username: '',
        email: '',
        firstname: '',
        lastname: '',
        phoneNumber: '',
        birthdate: '',  // Add birthdate field
    });
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserData = async () => {
            const user = JSON.parse(localStorage.getItem('user'));
            if (user) {
                setUserData(user); // Populate the form with user data
            } else {
                console.error('No user data found, user not authenticated.');
                navigate('/login'); // Redirect to login if not authenticated
            }
        };

        fetchUserData();
    }, [navigate]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData({
            ...userData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token'); // Assuming token is stored in localStorage

        // Log user data and token for debugging
        console.log('User Data being sent:', userData);
        console.log('Token being sent:', token);

        try {
            const response = await axiosInstance.put('/user/update', userData, {
                headers: {
                    Authorization: `Bearer ${token}`, // Pass the token in the request header
                },
            });
            console.log('Profile updated:', response.data);
            alert('Profile updated successfully!');

            // Update localStorage with the new user data
            localStorage.setItem('user', JSON.stringify(response.data));

            // Redirect to the dashboard after successful update
            navigate('/dashboard');
        } catch (error) {
            console.error('Error updating profile:', error.response ? error.response.data : error.message);
            // Consider displaying an alert to inform the user of the error
        }
    };

    return (
        <div>
            <h2>Update Profile</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input type="text" name="username" value={userData.username} onChange={handleChange} required />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" value={userData.email} onChange={handleChange} required />
                </div>
                <div>
                    <label>First Name:</label>
                    <input type="text" name="firstname" value={userData.firstname} onChange={handleChange} required />
                </div>
                <div>
                    <label>Last Name:</label>
                    <input type="text" name="lastname" value={userData.lastname} onChange={handleChange} required />
                </div>
                <div>
                    <label>Phone Number:</label>
                    <input type="text" name="phoneNumber" value={userData.phoneNumber} onChange={handleChange} />
                </div>
                <div>
                    <label>Date of Birth:</label>
                    <input type="date" name="birthdate" value={userData.birthdate} onChange={handleChange} />
                </div>
                <button type="submit">Update Profile</button>
            </form>
        </div>
    );
};

export default UpdateProfile;
