import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import axiosInstance from '../axiosConfig';
import jwtDecode from 'jwt-decode'; // Ensure this is installed
import { FaUserCircle } from 'react-icons/fa'; // Importing an icon (you can choose any)

const Dashboard = () => {
  const [userData, setUserData] = useState(null);
  const [role, setRole] = useState('');
  const navigate = useNavigate(); // Initialize navigate

  useEffect(() => {
    const fetchUserData = () => {
      const user = JSON.parse(localStorage.getItem('user')); // Get user data from local storage
      if (user) {
        setUserData(user);
        setRole(user.roles[0]); // Set the role from user data
      } else {
        console.error('No user data found, user not authenticated.');
      }
    };

    fetchUserData();
  }, []);

  // Function to navigate to the user's profile page
  const goToProfile = () => {
    navigate('/profile'); // Change to the actual path of your profile page
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'flex-end', padding: '10px' }}>
        <div style={{ textAlign: 'center', cursor: 'pointer' }} onClick={goToProfile}>
          <FaUserCircle size={40} /> {/* Profile icon */}
          <div>{userData ? userData.firstName : ''}</div> {/* Display user's first name */}
        </div>
      </div>

      <h2>User Dashboard</h2>
      {role === 'ROLE_ADMIN' ? (
        <h2>Admin Dashboard</h2>
      ) : (
        <h2>User Dashboard</h2>
      )}
      {userData && (
        <div>
          <p>Username: {userData.username}</p>
          <p>Email: {userData.email}</p>
          <p>Full Name: {userData.firstName} {userData.lastName}</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
