import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../axiosConfig';
import { FaUserCircle, FaCog } from 'react-icons/fa';
import {
    Box,
    Button,
    Divider,
    Typography,
    IconButton,
    List,
    ListItem,
    ListItemText,
    CircularProgress,
} from '@mui/material';
import { styled } from '@mui/system';

const StyledContainer = styled(Box)({
    display: 'flex',
    flexDirection: 'row',
    padding: '0', // Remove padding to eliminate white space
    margin: '0', // Remove margins
    width: '100vw', // Full width
    height: '100vh', // Full height
    position: 'fixed', // Ensure it starts from the top
    top: 0,
    left: 0,
    overflow: 'hidden', // Prevent overflow
    backgroundColor: '#f0f4ff', // Light background for the whole layout
});

const Sidebar = styled(Box)(({ theme }) => ({
    width: '20%', // Sidebar width
    maxWidth: '350px',
    height: '100vh', // Full viewport height
    borderRight: '1px solid #ccc',
    padding: '20px',
    backgroundColor: '#d4e6ff',
    flexShrink: 0,
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)', // Subtle shadow for sidebar
    [theme.breakpoints.down('sm')]: {
        width: '30%',
    },
    [theme.breakpoints.down('xs')]: {
        display: 'none',
    },
}));

const SidebarButton = styled(IconButton)({
    marginBottom: '10px',
});

const MainContent = styled(Box)({
    flexGrow: 1,
    height: '100vh', // Ensure content height is the full viewport height
    padding: '20px',
    textAlign: 'center',
    backgroundColor: '#ffffff',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)', // Slight shadow for content area
    overflowY: 'auto',
    borderRadius: '10px', // Smooth corners for content area
    margin: '20px', // Add margin to avoid flush against edges
});

const Suggestions = styled(Box)(({ theme }) => ({
    width: '20%', // Suggestions width
    maxWidth: '250px',
    height: '100vh', // Full viewport height
    borderLeft: '1px solid #ccc',
    padding: '20px',
    backgroundColor: '#d4e6ff',
    flexShrink: 0,
    boxShadow: '-2px 0 5px rgba(0, 0, 0, 0.1)', // Subtle shadow for suggestions panel
    [theme.breakpoints.down('sm')]: {
        width: '30%',
    },
    [theme.breakpoints.down('xs')]: {
        display: 'none',
    },
}));

const Dashboard = () => {
    const [userData, setUserData] = useState(null);
    const [role, setRole] = useState('');
    const [friends, setFriends] = useState([]);
    const [peopleYouMayKnow, setPeopleYouMayKnow] = useState([]);
    const [settingsVisible, setSettingsVisible] = useState(false);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('token');
            const userId = JSON.parse(localStorage.getItem('user'))?.id;

            if (token && userId) {
                try {
                    const response = await axiosInstance.get(`/api/user/id/${userId}`, {
                        headers: { Authorization: `Bearer ${token}` },
                    });
                    setUserData(response.data);
                    setRole(response.data.roles?.[0] || 'Guest');
                    await fetchFriends(userId, token);
                    await fetchPeopleYouMayKnow(userId, token);
                } catch (error) {
                    console.error('Failed to fetch user data:', error.response ? error.response.data : error.message);
                    navigate('/login');
                } finally {
                    setLoading(false);
                }
            } else {
                navigate('/login');
            }
        };

        fetchUserData();
    }, [navigate]);

    const fetchFriends = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/api/user/friends/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setFriends(response.data);
        } catch (error) {
            console.error('Failed to fetch friends:', error.response ? error.response.data : error.message);
        }
    };

    const fetchPeopleYouMayKnow = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/user-service/api/friend-request/not-friends/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setPeopleYouMayKnow(response.data);
        } catch (error) {
            console.error('Failed to fetch people you may know:', error.response ? error.response.data : error.message);
        }
    };

    const toggleSettings = () => {
        setSettingsVisible((prev) => !prev);
    };

    const handleUpdateProfile = () => {
        navigate('/update-profile');
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
    };

    const handleGoToInvitations = () => {
        navigate('/invitations');
    };

    if (loading) {
        return <CircularProgress />;
    }

    return (
        <StyledContainer>
            <Sidebar>
                <div>
                    <SidebarButton onClick={() => navigate('/profile')}>
                        <FaUserCircle size={50} />
                    </SidebarButton>
                    <Typography variant="h5">{userData ? userData.username : 'Guest'}</Typography>
                    <Divider sx={{ margin: '10px 0' }} />

                    <Typography variant="h6" sx={{ color: '##435670' }}>Friends List</Typography>
                    <List>
                        {friends.length > 0 ? (
                            friends.map((friend) => (
                                <ListItem key={friend.id}>
                                    <ListItemText primary={`${friend.firstname} ${friend.lastname}`} />
                                </ListItem>
                            ))
                        ) : (
                            <ListItem>
                                <ListItemText primary="No friends found." />
                            </ListItem>
                        )}
                    </List>
                </div>

                <div>
                    <SidebarButton onClick={toggleSettings}>
                        <FaCog size={20} />
                    </SidebarButton>

                    {settingsVisible && (
                        <Box>
                            <Button onClick={handleUpdateProfile} variant="outlined" sx={{ marginBottom: '5px' }}>
                                Update Profile
                            </Button>
                            <Button onClick={handleLogout} variant="outlined">
                                Logout
                            </Button>
                        </Box>
                    )}
                </div>
            </Sidebar>

            <MainContent>
                <Typography variant="h4" sx={{ color: '#004aad' }}>Welcome to your Dashboard!</Typography>
                <Typography variant="body1" sx={{ margin: '20px 0' }}>
                    This area can be used for various widgets or information.
                </Typography>
            </MainContent>

            <Suggestions>
                <Typography variant="h6" sx={{ color: '#004aad' }}>People You May Know</Typography>
                <List>
                    {peopleYouMayKnow.length > 0 ? (
                        peopleYouMayKnow.map((user) => (
                            <ListItem key={user.id}>
                                <ListItemText primary={user.username} />
                            </ListItem>
                        ))
                    ) : (
                        <ListItem>
                            <ListItemText primary="No suggestions." />
                        </ListItem>
                    )}
                </List>
                <Button onClick={handleGoToInvitations} variant="outlined" fullWidth>
                    Invitations
                </Button>
            </Suggestions>
        </StyledContainer>
    );
};

export default Dashboard;
