import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../axiosConfig';
import { FaUserCircle } from 'react-icons/fa';

const Dashboard = () => {
    const [userData, setUserData] = useState(null);
    const [role, setRole] = useState('');
    const [friends, setFriends] = useState([]); // State for friends list
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserData = async () => {
            const user = JSON.parse(localStorage.getItem('user'));
            if (user) {
                setUserData(user);
                setRole(user.roles[0]);
                await fetchFriends(user.id); // Fetch friends after user is set
            } else {
                console.error('No user data found, user not authenticated.');
            }
        };

        fetchUserData();
    }, []);

    // Function to fetch friends list
    const fetchFriends = async (userId) => {
        try {
            const response = await axiosInstance.get(`/user/friends/${userId}`);
            console.log('Friends fetched:', response.data); // Log friends data
            setFriends(response.data); // Set friends data
        } catch (error) {
            console.error('Failed to fetch friends:', error.response ? error.response.data : error.message);
        }
    };

    const goToProfile = () => {
        navigate('/profile'); // Navigate to profile page
    };

    return (
        <div style={{ display: 'flex' }}>
            <div style={{ width: '200px', borderRight: '1px solid #ccc', padding: '10px' }}>
                <FaUserCircle size={50} onClick={goToProfile} style={{ cursor: 'pointer' }} />
                <h3>{userData ? `${userData.username} ${userData.lastname}` : 'Guest'}</h3>
                <h4>Friends List</h4>
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {friends.length > 0 ? (
                        friends.map((friend) => (
                            <li key={friend.id}>{friend.firstname} {friend.lastname}</li>
                        ))
                    ) : (
                        <li>No friends found.</li>
                    )}
                </ul>
            </div>
            <div style={{ padding: '20px' }}>
                <h2>User Dashboard</h2>
                {role === 'ROLE_ADMIN' ? (
                    <h2>Admin Dashboard</h2>
                ) : (
                    <h2>User Dashboard</h2>
                )}
                {userData && (
                    <div>
                        <p>Username: {userData.username}</p>
                        <p>Email: {userData.email}</p>
                        <p>Full Name: {userData.firstname} {userData.lastname}</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
