import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../axiosConfig';
import { FaUserCircle, FaCog, FaArrowLeft } from 'react-icons/fa';

const Invitations = () => {
    const [role, setRole] = useState('');
    const [userData, setUserData] = useState(null);
    const [friends, setFriends] = useState([]);
    const [sentInvitations, setSentInvitations] = useState([]);
    const [receivedInvitations, setReceivedInvitations] = useState([]);
    const [peopleYouMayKnow, setPeopleYouMayKnow] = useState([]);
    const [settingsVisible, setSettingsVisible] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('token');
            const userId = JSON.parse(localStorage.getItem('user'))?.id;

            if (token && userId) {
                try {
                    const response = await axiosInstance.get(`/api/user/id/${userId}`, {
                        headers: {
                            Authorization: `Bearer ${token}`,
                        },
                    });
                    setUserData(response.data);
                    await fetchFriends(userId, token); // Fetch friends here
                    await fetchPeopleYouMayKnow(userId, token); // Fetch suggestions here
                    await fetchReceivedInvitations(userId, token);
                    await fetchSentInvitations(userId, token);
                } catch (error) {
                    console.error('Failed to fetch user data:', error.response ? error.response.data : error.message);
                    navigate('/login');
                }
            } else {
                navigate('/login');
            }
        };


        fetchUserData();
    }, [navigate]);

    const fetchFriends = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/api/user/friends/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            console.log('Fetched friends:', response.data); // Log the response
            setFriends(response.data);
        } catch (error) {
            console.error('Failed to fetch friends:', error.response ? error.response.data : error.message);
        }
    };


    const fetchPeopleYouMayKnow = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/user-service/api/friend-request/not-friends/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setPeopleYouMayKnow(response.data);
        } catch (error) {
            console.error('Failed to fetch people you may know:', error.response ? error.response.data : error.message);
        }
    };

    const fetchReceivedInvitations = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/user-service/api/friend-request/received/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setReceivedInvitations(response.data);
        } catch (error) {
            console.error('Failed to fetch received invitations:', error.response ? error.response.data : error.message);
        }
    };

    const fetchSentInvitations = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/user-service/api/friend-request/sent/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setSentInvitations(response.data); // Set sent invitations
        } catch (error) {
            console.error('Failed to fetch sent invitations:', error.response ? error.response.data : error.message);
        }
    };

    const acceptRequest = async (senderId, token) => {
        const receiverId = userData.id; // Current logged-in user's ID
        try {
            await axiosInstance.delete(`/user-service/api/friend-request/accept/${senderId}/${receiverId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert('Friend request accepted!');
            fetchReceivedInvitations(receiverId, token);
        } catch (error) {
            console.error('Error accepting friend request:', error);
        }
    };

    const rejectRequest = async (senderId, token) => {
        const receiverId = userData.id; // Current logged-in user's ID
        try {
            await axiosInstance.delete(`/user-service/api/friend-request/reject/${senderId}/${receiverId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert('Friend request rejected!');
            fetchReceivedInvitations(receiverId, token);
        } catch (error) {
            console.error('Error rejecting friend request:', error);
        }
    };

    const goToProfile = () => {
        navigate(`/profile`); // Adjust the route as necessary
    };

    const toggleSettings = () => {
        setSettingsVisible((prev) => !prev);
    };

    const handleUpdateProfile = () => {
        navigate('/update-profile');
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login'); // Redirect to login page
    };

    const handleGoBack = () => {
        navigate('/dashboard'); // Redirect to Dashboard page
    };

    return (
        <div style={{ display: 'flex' }}>
            <div style={{ width: '250px', borderRight: '1px solid #ccc', padding: '20px' }}>
                <FaUserCircle size={50} onClick={goToProfile} style={{ cursor: 'pointer', marginBottom: '10px' }} />
                <h3>{userData ? `${userData.username}` : 'Guest'}</h3>

                {/* Friends List */}
                <h4>Friends List</h4>
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {friends.length > 0 ? (
                        friends.map((friend) => (
                            <li key={friend.id}>{friend.firstname} {friend.lastname}</li>
                        ))
                    ) : (
                        <li>No friends found.</li>
                    )}
                </ul>


                {/* Settings Icon and Menu */}
                <FaCog size={20} onClick={toggleSettings} style={{ cursor: 'pointer', marginTop: '10px' }} />
                {settingsVisible && (
                    <div style={{ marginTop: '10px' }}>
                        <button onClick={handleUpdateProfile}>Update Profile</button>
                        <button onClick={handleLogout}>Logout</button>
                    </div>
                )}
            </div>

            <div style={{ flexGrow: 1, padding: '20px', textAlign: 'center' }}>
                <button onClick={handleGoBack}>
                    <FaArrowLeft /> Go Back
                </button>
                <h1>Invitations</h1>

                {/* Center section: Received Invitations and Sent Invitations */}
                <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '20px' }}>
                    <div style={{ width: '45%' }}>
                        <h4>Received Invitations</h4>
                        <ul style={{ listStyleType: 'none', padding: 0 }}>
                            {receivedInvitations.length > 0 ? (
                                receivedInvitations.map((invitation) => (
                                    <li key={invitation.senderId}>
                                        {invitation.senderUsername}
                                        <button onClick={() => acceptRequest(invitation.senderId)}>Accept</button>
                                        <button onClick={() => rejectRequest(invitation.senderId)}>Reject</button>
                                    </li>
                                ))
                            ) : (
                                <li>No received invitations.</li>
                            )}
                        </ul>
                    </div>

                    <div style={{ width: '45%' }}>
                        <h4>Sent Invitations</h4>
                        <ul style={{ listStyleType: 'none', padding: 0 }}>
                            {sentInvitations.length > 0 ? (
                                sentInvitations.map((invitation) => (
                                    <li key={invitation.receiverId}>
                                        {invitation.receiverUsername}
                                        {/* You can add buttons to cancel or manage sent invitations here if needed */}
                                    </li>
                                ))
                            ) : (
                                <li>No sent invitations.</li>
                            )}
                        </ul>
                    </div>
                </div>
            </div>

            <div style={{ width: '250px', borderLeft: '1px solid #ccc', padding: '20px' }}>
                <h4>People You May Know</h4>
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {peopleYouMayKnow.length > 0 ? (
                        peopleYouMayKnow.map((user) => (
                            <li key={user.id}>{user.username}</li>
                        ))
                    ) : (
                        <li>No suggestions.</li>
                    )}
                </ul>
            </div>
        </div>
    );
};

export default Invitations;
