import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../axiosConfig';
import { FaUserCircle, FaCog, FaArrowLeft } from 'react-icons/fa';
import {
    Box,
    Button,
    Divider,
    Typography,
    IconButton,
    List,
    ListItem,
    ListItemText,
    CircularProgress,
} from '@mui/material';
import { styled } from '@mui/system';

const StyledContainer = styled(Box)({
    display: 'flex',
    flexDirection: 'row',
    padding: '0',
    margin: '0',
    width: '100vw',
    height: '100vh',
    position: 'fixed',
    top: 0,
    left: 0,
    overflow: 'hidden',
    backgroundColor: '#f0f4ff',
});

const Sidebar = styled(Box)(({ theme }) => ({
    width: '20%',
    maxWidth: '350px',
    height: '100vh',
    borderRight: '1px solid #ccc',
    padding: '20px',
    backgroundColor: '#BED7DC',
    flexShrink: 0,
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)',
    [theme.breakpoints.down('sm')]: {
        width: '30%',
    },
    [theme.breakpoints.down('xs')]: {
        display: 'none',
    },
}));

const SidebarButton = styled(IconButton)({
    marginBottom: '10px',
});

const MainContent = styled(Box)({
    flexGrow: 1,
    height: '100vh',
    padding: '20px',
    backgroundColor: '#ffffff',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    overflowY: 'auto',
    borderRadius: '10px',
    margin: '20px',
});

const Invitations = () => {
    const [role, setRole] = useState('');
    const [userData, setUserData] = useState(null);
    const [friends, setFriends] = useState([]);
    const [sentInvitations, setSentInvitations] = useState([]);
    const [receivedInvitations, setReceivedInvitations] = useState([]);
    const [settingsVisible, setSettingsVisible] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('token');
            const userId = JSON.parse(localStorage.getItem('user'))?.id;

            if (token && userId) {
                try {
                    const response = await axiosInstance.get(`/api/user/id/${userId}`, {
                        headers: { Authorization: `Bearer ${token}` },
                    });
                    setUserData(response.data);
                    await fetchFriends(userId, token);
                    await fetchReceivedInvitations(userId, token);
                    await fetchSentInvitations(userId, token);
                } catch (error) {
                    console.error('Failed to fetch user data:', error.response ? error.response.data : error.message);
                    navigate('/login');
                }
            } else {
                navigate('/login');
            }
        };
        fetchUserData();
    }, [navigate]);

    const fetchFriends = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/api/user/friends/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setFriends(response.data);
        } catch (error) {
            console.error('Failed to fetch friends:', error.response ? error.response.data : error.message);
        }
    };

    const fetchReceivedInvitations = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/user-service/api/friend-request/received/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setReceivedInvitations(response.data);
        } catch (error) {
            console.error('Failed to fetch received invitations:', error.response ? error.response.data : error.message);
        }
    };

    const fetchSentInvitations = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/user-service/api/friend-request/sent/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setSentInvitations(response.data);
        } catch (error) {
            console.error('Failed to fetch sent invitations:', error.response ? error.response.data : error.message);
        }
    };

    const acceptRequest = async (senderId, token) => {
        const receiverId = userData.id;
        try {
            await axiosInstance.delete(`/user-service/api/friend-request/accept/${senderId}/${receiverId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert('Friend request accepted!');
            fetchReceivedInvitations(receiverId, token);
        } catch (error) {
            console.error('Error accepting friend request:', error);
        }
    };

    const rejectRequest = async (senderId, token) => {
        const receiverId = userData.id;
        try {
            await axiosInstance.delete(`/user-service/api/friend-request/reject/${senderId}/${receiverId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert('Friend request rejected!');
            fetchReceivedInvitations(receiverId, token);
        } catch (error) {
            console.error('Error rejecting friend request:', error);
        }
    };

    const toggleSettings = () => {
        setSettingsVisible((prev) => !prev);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
    };

    if (!userData) {
        return <CircularProgress />;
    }

    return (
        <StyledContainer>
            <Sidebar>
                <SidebarButton onClick={() => navigate('/dashboard')}>
                    <FaArrowLeft size={30} />
                </SidebarButton>
                <div>
                    <SidebarButton onClick={() => navigate('/profile')}>
                        <FaUserCircle size={50} />
                    </SidebarButton>
                    <Typography variant="h5">{userData.username}</Typography>
                    <Divider sx={{ margin: '10px 0' }} />
                    <Typography variant="h6" sx={{ color: '#183c43' }}>Friends List</Typography>
                    <List>
                        {friends.length > 0 ? (
                            friends.map((friend) => (
                                <ListItem key={friend.id}>
                                    <ListItemText primary={`${friend.firstname} ${friend.lastname}`} />
                                </ListItem>
                            ))
                        ) : (
                            <ListItem>
                                <ListItemText primary="No friends found." />
                            </ListItem>
                        )}
                    </List>
                </div>

                <SidebarButton onClick={toggleSettings}>
                    <FaCog size={20} />
                </SidebarButton>
                {settingsVisible && (
                    <Box>
                        <Button onClick={handleLogout} variant="outlined">Logout</Button>
                    </Box>
                )}
            </Sidebar>

            <MainContent>
                <Typography variant="h4" sx={{ color: '#284d55' }}>Invitations</Typography>
                <Typography variant="h6" sx={{ color: '#183c43', marginTop: '20px' }}>Received Invitations</Typography>
                <List>
                    {receivedInvitations.length > 0 ? (
                        receivedInvitations.map((invitation) => (
                            <ListItem key={invitation.id}>
                                <ListItemText primary={invitation.sender.username} />
                                <Button
                                    variant="outlined"
                                    onClick={() => acceptRequest(invitation.sender.id, localStorage.getItem('token'))}
                                    sx={{
                                        color: '#183c43',
                                        borderColor: '#183c43',
                                        marginRight: '10px',
                                    }}
                                >
                                    Accept
                                </Button>
                                <Button
                                    variant="outlined"
                                    onClick={() => rejectRequest(invitation.sender.id, localStorage.getItem('token'))}
                                    sx={{ color: '#183c43', borderColor: '#183c43' }}
                                >
                                    Reject
                                </Button>
                            </ListItem>
                        ))
                    ) : (
                        <ListItem>
                            <ListItemText primary="No received invitations." />
                        </ListItem>
                    )}
                </List>

                <Typography variant="h6" sx={{ color: '#183c43', marginTop: '20px' }}>Sent Invitations</Typography>
                <List>
                    {sentInvitations.length > 0 ? (
                        sentInvitations.map((invitation) => (
                            <ListItem key={invitation.id}>
                                <ListItemText primary={invitation.receiver.username} />
                            </ListItem>
                        ))
                    ) : (
                        <ListItem>
                            <ListItemText primary="No sent invitations." />
                        </ListItem>
                    )}
                </List>
            </MainContent>
        </StyledContainer>
    );
};

export default Invitations;
