import React, { useEffect, useState } from 'react';
import axiosInstance from '../axiosConfig';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
    const [userData, setUserData] = useState(null);
    const [role, setRole] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('token'); // Get the token
            const user = JSON.parse(localStorage.getItem('user')); // Retrieve user info

            if (token && user && user.id) {
                try {
                    // Fetch the latest user data using the user ID
                    const response = await axiosInstance.get(`/user/id/${user.id}`, {
                        headers: {
                            Authorization: `Bearer ${token}`, // Pass the token in the request header
                        },
                    });
                    const fetchedUserData = response.data;
                    setUserData(fetchedUserData); // Update state with the latest user data
                    setRole(fetchedUserData.roles[0]); // Assuming roles is an array

                    // Update localStorage with the latest user data
                    localStorage.setItem('user', JSON.stringify(fetchedUserData));
                } catch (error) {
                    console.error('Failed to fetch user data:', error.response ? error.response.data : error.message);
                    if (error.response && error.response.status === 401) {
                        navigate('/login'); // Redirect to login if unauthorized
                    }
                }
            } else {
                navigate('/login'); // Redirect to login if not authenticated
            }
        };

        fetchUserData();
    }, [navigate]);

    return (
        <div>
            <h2>{role === 'ROLE_ADMIN' ? 'Admin Dashboard' : 'User Dashboard'}</h2>
            {userData && (
                <div>
                    <p>Username: {userData.username}</p>
                    <p>Email: {userData.email}</p>
                    <p>Full Name: {userData.firstname} {userData.lastname}</p>
                    <p>Date of Birth: {userData.birthdate}</p> {/* Display birthdate */}
                </div>
            )}
        </div>
    );
};

export default Dashboard;
