import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../axiosConfig';
import { FaUserCircle, FaCog } from 'react-icons/fa';
import {
    Box,
    Button,
    Divider,
    Typography,
    IconButton,
    List,
    ListItem,
    ListItemText,
    CircularProgress,
    TextField,
} from '@mui/material';
import { styled } from '@mui/system';

const StyledContainer = styled(Box)({
    display: 'flex',
    flexDirection: 'row',
    padding: '0',
    margin: '0',
    width: '100vw',
    height: '100vh',
    position: 'fixed',
    top: 0,
    left: 0,
    overflow: 'hidden',
    backgroundColor: '#DDE6ED',
});
const Sidebar = styled(Box)(({ theme }) => ({
    width: '20%',
    maxWidth: '350px',
    height: '100vh',
    borderRight: '1px solid #ccc',
    padding: '20px',
    backgroundColor: '#c3d0d9',
    flexShrink: 0,
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)',
    [theme.breakpoints.down('sm')]: {
        width: '30%',
    },
    [theme.breakpoints.down('xs')]: {
        display: 'none',
    },
}));

const MainContent = styled(Box)({
    flexGrow: 1,
    height: '100vh',
    padding: '20px',
    textAlign: 'center',
    backgroundColor: '#ffffff',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    overflowY: 'auto',
    borderRadius: '10px',
    margin: '20px',
});

const Suggestions = styled(Box)(({ theme }) => ({
    width: '30%', // Increased width to 30%
    maxWidth: '350px', // Optional: Set a maximum width
    height: '100vh',
    borderLeft: '1px solid #ccc',
    padding: '20px',
    backgroundColor: '#c3d0d9',
    flexShrink: 0,
    boxShadow: '-2px 0 5px rgba(0, 0, 0, 0.1)',
    display: 'flex', // Ensure the panel displays correctly
    flexDirection: 'column', // Maintain column layout
    [theme.breakpoints.down('sm')]: {
        width: '40%', // Adjust width on smaller screens if necessary
    },
    [theme.breakpoints.down('xs')]: {
        display: 'none',
    },
}));


const ContentBox = styled(Box)({
    backgroundColor: '#ffffff',
    padding: '15px',
    borderRadius: '10px',
    boxShadow: '0px 2px 5px rgba(0, 0, 0, 0.1)',
    marginBottom: '10px',
});

const SidebarButton = styled(IconButton)({
    marginBottom: '10px',
});


const Dashboard = () => {
    const [userData, setUserData] = useState(null);
    const [role, setRole] = useState('');
    const [friends, setFriends] = useState([]);
    const [peopleYouMayKnow, setPeopleYouMayKnow] = useState([]);
    const [settingsVisible, setSettingsVisible] = useState(false);
    const [loading, setLoading] = useState(true);

    // State for conversation and messages
    const [currentConversationId, setCurrentConversationId] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');

    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('token');
            const userId = JSON.parse(localStorage.getItem('user'))?.id;

            if (token && userId) {
                try {
                    const response = await axiosInstance.get(`/api/user/id/${userId}`, {
                        headers: { Authorization: `Bearer ${token}` },
                    });
                    setUserData(response.data);
                    setRole(response.data.roles?.[0] || 'Guest');
                    await fetchFriends(userId, token);
                    await fetchPeopleYouMayKnow(userId, token);
                } catch (error) {
                    console.error('Failed to fetch user data:', error.response ? error.response.data : error.message);
                    navigate('/login');
                } finally {
                    setLoading(false);
                }
            } else {
                navigate('/login');
            }
        };

        fetchUserData();
    }, [navigate]);

    const fetchFriends = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/api/user/friends/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setFriends(response.data);
        } catch (error) {
            console.error('Failed to fetch friends:', error.response ? error.response.data : error.message);
        }
    };

    const fetchPeopleYouMayKnow = async (userId, token) => {
        try {
            const response = await axiosInstance.get(`/user-service/api/friend-request/not-friends/${userId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setPeopleYouMayKnow(response.data);
        } catch (error) {
            console.error('Failed to fetch people you may know:', error.response ? error.response.data : error.message);
        }
    };

    const fetchMessages = async (friendId) => {
        const token = localStorage.getItem('token');
        try {
            const response = await axiosInstance.get(`/api/messages/${friendId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setMessages(response.data);
            setCurrentConversationId(friendId);
        } catch (error) {
            console.error('Failed to fetch messages:', error.response ? error.response.data : error.message);
        }
    };

    const handleSendMessage = async () => {
        const token = localStorage.getItem('token');
        const userId = JSON.parse(localStorage.getItem('user'))?.id;

        if (newMessage && token && currentConversationId) {
            try {
                await axiosInstance.post(`/api/messages/${currentConversationId}`, {
                    senderId: userId,
                    content: newMessage,
                }, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setMessages((prev) => [...prev, { senderId: userId, content: newMessage }]); // Update messages state
                setNewMessage(''); // Clear input
            } catch (error) {
                console.error('Failed to send message:', error.response ? error.response.data : error.message);
            }
        }
    };

    const toggleSettings = () => {
        setSettingsVisible((prev) => !prev);
    };

    const handleUpdateProfile = () => {
        navigate('/update-profile');
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
    };

    const handleGoToInvitations = () => {
        navigate('/invitations');
    };

    const handleSendFriendRequest = async (friendId) => {
        const token = localStorage.getItem('token');
        const userId = JSON.parse(localStorage.getItem('user'))?.id;

        if (token && userId) {
            try {
                await axiosInstance.post(`/user-service/api/friend-request/${userId}/${friendId}`, {}, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                alert('Friend request sent successfully!');
            } catch (error) {
                console.error('Failed to send friend request:', error.response ? error.response.data : error.message);
                alert('Failed to send friend request. Please try again later.');
            }
        }
    };

    if (loading) {
        return <CircularProgress />;
    }

    return (
        <StyledContainer>
            <Sidebar>
                <div>
                    <SidebarButton onClick={() => navigate('/profile')}>
                        <FaUserCircle size={50} />
                    </SidebarButton>
                    <Typography variant="h5">{userData ? userData.username : 'Guest'}</Typography>
                    <Divider sx={{ margin: '10px 0' }} />

                    <ContentBox>
                        <Typography variant="h6" sx={{ color: '#27374D' }}>Friends List</Typography>
                        <List>
                            {friends.length > 0 ? (
                                friends.map((friend) => (
                                    <ListItem key={friend.id} button onClick={() => fetchMessages(friend.id)}>
                                        <ListItemText primary={`${friend.firstname} ${friend.lastname}`} />
                                    </ListItem>
                                ))
                            ) : (
                                <ListItem>
                                    <ListItemText primary="No friends found." />
                                </ListItem>
                            )}
                        </List>
                    </ContentBox>
                </div>

                <div>
                    <SidebarButton onClick={toggleSettings}>
                        <FaCog size={20} />
                    </SidebarButton>

                    {settingsVisible && (
                        <Box>
                            <Button onClick={handleUpdateProfile} variant="outlined" sx={{ marginBottom: '5px' }}>
                                Update Profile
                            </Button>
                            <Button onClick={handleLogout} variant="outlined">
                                Logout
                            </Button>
                        </Box>
                    )}
                </div>
            </Sidebar>

            <MainContent>
                <Typography variant="h4" sx={{ color: '#27374D' }}>Welcome to your Dashboard!</Typography>
                <Typography variant="body1" sx={{ margin: '20px 0' }}>
                    This area can be used for various widgets or information.
                </Typography>

                {/* Conversation Section */}
                {currentConversationId && (
                    <Box>
                        <Typography variant="h5">Conversation with {currentConversationId}</Typography>
                        <List>
                            {messages.map((msg, index) => (
                                <ListItem key={index} sx={{ justifyContent: msg.senderId === JSON.parse(localStorage.getItem('user')).id ? 'flex-end' : 'flex-start' }}>
                                    <ListItemText primary={msg.content} />
                                </ListItem>
                            ))}
                        </List>
                        <TextField
                            variant="outlined"
                            fullWidth
                            placeholder="Type a message..."
                            value={newMessage}
                            onChange={(e) => setNewMessage(e.target.value)}
                            sx={{ marginBottom: '10px' }}
                        />
                        <Button variant="contained" onClick={handleSendMessage}>
                            Send
                        </Button>
                    </Box>
                )}
            </MainContent>

            <Suggestions>
                <ContentBox>
                    <Typography variant="h6" sx={{ color: '#27374D' }}>People You May Know</Typography>
                    <List>
                        {peopleYouMayKnow.length > 0 ? (
                            peopleYouMayKnow.map((user) => (
                                <ListItem key={user.id}>
                                    <ListItemText primary={user.username} />
                                    <Button 
                                        variant="outlined" 
                                        size="small" 
                                        onClick={() => handleSendFriendRequest(user.id)}
                                    >
                                        Add Friend
                                    </Button>
                                </ListItem>
                            ))
                        ) : (
                            <ListItem>
                                <ListItemText primary="No suggestions available." />
                            </ListItem>
                        )}
                    </List>
                </ContentBox>
            </Suggestions>
        </StyledContainer>
    );
};

export default Dashboard;
